meshConfig.additionalTypeDefs:
type Citisignal_CategoryItem {
  id: String!
  name: String!
  urlPath: String!
  urlKey: String!
  level: Int!
  position: Int!
  includeInMenu: Boolean!
  isActive: Boolean!
  children: [Citisignal_CategoryItem!]!
  productCount: Int!
  parentId: String
  href: String!
  label: String!
}

type Citisignal_NavItem {
  href: String!
  label: String!
  category: String
}

type Citisignal_FooterNavItem {
  href: String!
  label: String!
}

type Citisignal_CategoryNavigationResponse {
  items: [Citisignal_CategoryItem!]!
  headerNav: [Citisignal_NavItem!]!
  footerNav: [Citisignal_FooterNavItem!]!
}

type Citisignal_BreadcrumbItem {
  categoryId: String
  name: String!
  urlPath: String!
  level: Int!
}

type Citisignal_BreadcrumbResponse {
  items: [Citisignal_BreadcrumbItem!]!
}

extend type Query {
  Citisignal_categoryNavigation(
    rootCategoryId: String
    includeInactive: Boolean
  ): Citisignal_CategoryNavigationResponse

  Citisignal_categoryBreadcrumbs(
    categoryUrlKey: String!
  ): Citisignal_BreadcrumbResponse
}

type Citisignal_CategoryInfo {
  id: String
  name: String!
  urlKey: String!
  description: String
  metaTitle: String
  metaDescription: String
}

type Citisignal_CategoryPageData {
  navigation: Citisignal_CategoryNavigationResponse!
  products: Citisignal_ProductCardResult!
  facets: Citisignal_ProductFacetsResult!
  breadcrumbs: Citisignal_BreadcrumbResponse!
  categoryInfo: Citisignal_CategoryInfo!
}

extend type Query {
  Citisignal_categoryPageData(
    category: String!  # Required for category pages
    phrase: String
    filter: Citisignal_PageFilter  # Uses PageFilter without category field
    sort: Citisignal_SortInput
    pageSize: Int
    currentPage: Int
  ): Citisignal_CategoryPageData
}

extend type Catalog_ComplexProductView {
  manufacturer: String
  memory_options: [String]
  available_colors: [Citisignal_ColorOption]
  is_on_sale: Boolean
  display_price: Float
  display_currency: String
  discount_percentage: Float
  in_stock: Boolean
  secure_image: Catalog_ProductViewImage
  secure_images: [Catalog_ProductViewImage]
}

extend type Catalog_SimpleProductView {
  manufacturer: String
  is_on_sale: Boolean
  display_price: Float
  display_currency: String
  discount_percentage: Float
  in_stock: Boolean
  secure_image: Catalog_ProductViewImage
  secure_images: [Catalog_ProductViewImage]
}

type Citisignal_ColorOption {
  name: String
  hex: String
}

type Citisignal_ProductImage {
  url: String
  altText: String
}

type Citisignal_ProductCard {
  id: String!
  sku: String!
  urlKey: String!
  name: String!
  manufacturer: String
  price: String!
  originalPrice: String
  discountPercent: Int
  inStock: Boolean!
  image: Citisignal_ProductImage     
  memory: [String]   
  colors: [Citisignal_ColorOption]
}

type Citisignal_PageInfo {
  current_page: Int!
  page_size: Int!
  total_pages: Int!
}

type Citisignal_ProductCardResult {
  items: [Citisignal_ProductCard]
  totalCount: Int
  hasMoreItems: Boolean!
  currentPage: Int
  page_info: Citisignal_PageInfo
  facets: [Citisignal_Facet]  # Only populated on page 1 to reduce duplication
  aggregations: [Citisignal_Facet]  # Alias for facets to match frontend expectations
  _debug: String  # Debug information when requested
}

input Citisignal_ProductFilter {
  category: String
  manufacturer: String
  memory: [String]
  colors: [String]
  priceMin: Float
  priceMax: Float
  onSaleOnly: Boolean
}

input Citisignal_PageFilter {
  manufacturer: String
  memory: [String]
  colors: [String]
  priceMin: Float
  priceMax: Float
  onSaleOnly: Boolean
}

enum Citisignal_SortDirection {
  ASC
  DESC
}

enum Citisignal_ProductSortAttribute {
  RELEVANCE     # Default for search results
  PRICE         # Sort by price
  NAME          # Sort by product name
}

input Citisignal_SortInput {
  attribute: Citisignal_ProductSortAttribute!
  direction: Citisignal_SortDirection!
}

extend type Query {
  Citisignal_productCards(
    phrase: String
    filter: Citisignal_ProductFilter
    limit: Int = 20
    page: Int = 1
    sort: Citisignal_SortInput
  ): Citisignal_ProductCardResult
}

type Citisignal_FacetOption {
  id: String!
  name: String!
  count: Int!
}

type Citisignal_Facet {
  title: String!
  key: String!
  type: String!
  options: [Citisignal_FacetOption]!
}

type Citisignal_ProductFacetsResult {
  facets: [Citisignal_Facet]!
  totalCount: Int
}

extend type Query {
  Citisignal_productFacets(
    phrase: String
    filter: Citisignal_ProductFilter
  ): Citisignal_ProductFacetsResult
}

type Citisignal_ProductPageData {
  navigation: Citisignal_CategoryNavigationResponse!
  products: Citisignal_ProductCardResult!
  facets: Citisignal_ProductFacetsResult!
  breadcrumbs: Citisignal_BreadcrumbResponse!
  _debug: String  # Debug information when requested
}

extend type Query {
  Citisignal_productPageData(
    category: String  # Optional - for product listings within a category
    phrase: String
    filter: Citisignal_PageFilter  # Uses PageFilter without category field
    sort: Citisignal_SortInput
    pageSize: Int
    currentPage: Int
  ): Citisignal_ProductPageData
}

type Citisignal_ProductSuggestion {
  id: String!
  name: String!
  sku: String!
  urlKey: String!
  price: String
  image: String
}

type Citisignal_SearchSuggestionsResult {
  suggestions: [Citisignal_ProductSuggestion]!
  totalCount: Int
}

extend type Query {
  Citisignal_searchSuggestions(
    phrase: String!
  ): Citisignal_SearchSuggestionsResult
}
