meshConfig.additionalTypeDefs:
type ColorOption {
  name: String
  hex: String
}

type ProductImage {
  url: String
  altText: String
}

type ProductCard {
  id: String!
  sku: String!
  urlKey: String!
  name: String!
  manufacturer: String
  price: String!
  originalPrice: String
  discountPercent: Int
  inStock: Boolean!
  image: ProductImage     
  memory: [String]   
  colors: [ColorOption]
}

type FacetOption {
  label: String!
  value: String!
  count: Int!
}

type Facet {
  attribute: String!
  label: String!
  options: [FacetOption]!
}

type ProductCardResult {
  items: [ProductCard]
  totalCount: Int
  hasMoreItems: Boolean!
  currentPage: Int
  facets: [Facet]
}

input Citisignal_ProductFilter {
  category: String
  manufacturer: String
  memory: [String]
  colors: [String]
  priceMin: Float
  priceMax: Float
  onSaleOnly: Boolean
}

enum SortDirection {
  ASC
  DESC
}

enum ProductSortAttribute {
  RELEVANCE     # Default for search results
  PRICE         # Sort by price
  NAME          # Sort by product name
}

input Citisignal_SortInput {
  attribute: ProductSortAttribute!
  direction: SortDirection!
}

extend type Query {
  Citisignal_productCards(
    phrase: String
    filter: Citisignal_ProductFilter
    limit: Int = 20
    page: Int = 1
    facets: Boolean = false
    sort: Citisignal_SortInput
  ): ProductCardResult
}

type ProductSuggestion {
  id: String!
  name: String!
  sku: String!
  urlKey: String!
  price: String
  image: String
}

type SearchSuggestionsResult {
  suggestions: [ProductSuggestion]!
  totalCount: Int
}

extend type Query {
  Citisignal_searchSuggestions(
    phrase: String!
  ): SearchSuggestionsResult
}

extend type Catalog_ComplexProductView {
  manufacturer: String
  memory_options: [String]
  available_colors: [ColorOption]
  is_on_sale: Boolean
  display_price: Float
  display_currency: String
  discount_percentage: Float
  in_stock: Boolean
  secure_image: Catalog_ProductViewImage
  secure_images: [Catalog_ProductViewImage]
}

extend type Catalog_SimpleProductView {
  manufacturer: String
  is_on_sale: Boolean
  display_price: Float
  display_currency: String
  discount_percentage: Float
  in_stock: Boolean
  secure_image: Catalog_ProductViewImage
  secure_images: [Catalog_ProductViewImage]
}
